namespace CleanArchitecture.Blazor.Application.Features.Projects.Specifications;
#nullable disable warnings
/// <summary>
/// Specification class for advanced filtering of Projects.
/// </summary>
public class ProjectAdvancedSpecification : Specification<Project>
{
    public ProjectAdvancedSpecification(ProjectAdvancedFilter filter)
    {
        DateTime today = DateTime.UtcNow;
        var todayrange = today.GetDateRange(ProjectListView.TODAY.ToString(), filter.CurrentUser.LocalTimeOffset);
        var last30daysrange = today.GetDateRange(ProjectListView.LAST_30_DAYS.ToString(),filter.CurrentUser.LocalTimeOffset);

        Query.Where(q => q.Name != null)
             .Where(filter.Keyword,!string.IsNullOrEmpty(filter.Keyword))
             .Where(q => q.CreatedById == filter.CurrentUser.UserId, filter.ListView == ProjectListView.My && filter.CurrentUser is not null)
             .Where(x => x.CreatedAt >= todayrange.Start && x.CreatedAt < todayrange.End.AddDays(1), filter.ListView == ProjectListView.TODAY)
             .Where(x => x.CreatedAt >= last30daysrange.Start, filter.ListView == ProjectListView.LAST_30_DAYS);
       
    }
}
