// Usage:
// This validator is used to ensure that a CreatePurchaseInvoiceCommand meets the required
// validation criteria. It enforces constraints like maximum field lengths and 
// ensures that the Name field is not empty before proceeding with the command execution.

namespace CleanArchitecture.Blazor.Application.Features.PurchaseInvoices.Commands.Create;

public class CreatePurchaseInvoiceCommandValidator : AbstractValidator<CreatePurchaseInvoiceCommand>
{
        public CreatePurchaseInvoiceCommandValidator()
        {
                RuleFor(v => v.SupplierId).NotNull(); 
    RuleFor(v => v.InvoiceNumber).MaximumLength(255); 
    RuleFor(v => v.InvoiceDate).NotNull(); 
    RuleFor(v => v.InvoiceType).MaximumLength(255); 
    RuleFor(v => v.TotalAmount).NotNull(); 
    RuleFor(v => v.VATAmount).NotNull(); 
    RuleFor(v => v.PaymentStatus).MaximumLength(255); 
    RuleFor(v => v.PaymentMethod).MaximumLength(255); 
    RuleFor(v => v.IBAN).MaximumLength(255); 
    RuleFor(v => v.SWIFT).MaximumLength(255); 
    RuleFor(v => v.Notes).MaximumLength(255); 

        }
       
}

