public class DeleteQuoteCommandHandler : 
             IRequestHandler<DeleteQuoteCommand, Result>

{
    private readonly IApplicationDbContext _context;
    public DeleteQuoteCommandHandler(
        IApplicationDbContext context)
    {
        _context = context;
    }
    public async Task<Result> Handle(DeleteQuoteCommand request, CancellationToken cancellationToken)
    {
        var items = await _context.Quotes.Where(x=>request.Id.Contains(x.Id)).ToListAsync(cancellationToken);
        foreach (var item in items)
        {
		    // raise a delete domain event
			item.AddDomainEvent(new QuoteDeletedEvent(item));
            _context.Quotes.Remove(item);
        }
        await _context.SaveChangesAsync(cancellationToken);
        return await Result.SuccessAsync();
    }

